import { ThemeContext } from 'src/ThemeContext'; // adjust the path as necessary

const ThemeToggle = () => {
  const { toggleTheme } = useContext(ThemeContext);

  return (
    <button onClick={toggleTheme}>Toggle Theme</button>
  );
};

export default ThemeToggle;
