import React, { useState } from 'react';
import axios from 'axios';

const TranslateComponent = ({ text, setOriginalText, apiKey }) => {
  const [isTranslated, setIsTranslated] = useState(false);

  const translateText = async (textToTranslate, targetLanguage) => {
    try {
      const response = await axios.post(
        'https://translation.googleapis.com/language/translate/v2',
        {},
        {
          params: {
            q: textToTranslate,
            target: targetLanguage,
            key: apiKey
          }
        }
      );
      return response.data.data.translations[0].translatedText;
    } catch (error) {
      console.error('Error translating text:', error);
      return textToTranslate;
    }
  };

  const handleTranslationClick = async () => {
    if (!isTranslated) {
      // If not translated yet, translate the text
      const translatedText = await translateText(text, 'ar');
      setOriginalText(translatedText); // Update the text displayed in the parent component
      setIsTranslated(true); // Update the state to reflect that text is now translated
    } else {
      // If already translated, revert to the original text
      setOriginalText(text); // Revert the text displayed in the parent component
      setIsTranslated(false); // Update the state to reflect that text is now original
    }
  };

  return (
    <button onClick={handleTranslationClick}>
      {isTranslated ? 'Revert to Original' : 'Translate to Arabic'}
    </button>
  );
};

export default TranslateComponent;
