// useTranslate.js (Custom Hook)
import { useState } from 'react';
import { translateText } from './TranslateComponent';

export function useTranslate(apiKey, initialText) {
  const [text, setText] = useState(initialText);
  const [clickCount, setClickCount] = useState(0);

  const handleTranslationClick = async () => {
    const targetLanguage = clickCount % 2 === 0 ? 'ar' : 'en'; // Arabic on odd clicks, English on even clicks
    const translatedText = await translateText(text, targetLanguage, apiKey);
    setText(translatedText);
    setClickCount(clickCount + 1); // Increment click count
  };

  return [text, handleTranslationClick];
}
