import React, { useState } from 'react';
import axios from 'axios';

const Weather = () => {
  const [city, setCity] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const API_KEY = 'NZFg01GFjycMlynXdI9PhAssjfKPnmeA';

  const fetchWeather = async () => {
    try {
      const response = await axios.get(
        `https://api.tomorrow.io/v4/timelines?location=${city}&fields=temperature&timesteps=current&apikey=${API_KEY}`
      );
      setWeatherData(response.data);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        placeholder="Enter city"
      />
      <button onClick={fetchWeather}>Get Weather</button>
      {weatherData && (
        <div>
          <h2>{city}</h2>
          <p>Temperature: {weatherData.data.timelines[0].intervals[0].values.temperature}Â°C</p>
        </div>
      )}
    </div>
  );
};

export default Weather;
