import React, { useState } from 'react';
import axios from 'axios';

const TranslateComponent = ({ text, apiKey }) => {
  const [translatedText, setTranslatedText] = useState(text);
  const [isArabic, setIsArabic] = useState(false);

  const translate = async (textToTranslate, targetLanguage) => {
    try {
      const response = await axios.post(
        'https://translation.googleapis.com/language/translate/v2',
        {},
        {
          params: {
            q: textToTranslate,
            target: targetLanguage,
            key: apiKey
          }
        }
      );
      return response.data.data.translations[0].translatedText;
    } catch (error) {
      console.error('Error translating text:', error);
      return textToTranslate; // Return the original text if translation fails
    }
  };

  const toggleTranslation = async () => {
    const targetLanguage = isArabic ? 'en' : 'ar';
    const translation = await translate(text, targetLanguage);
    setTranslatedText(translation);
    setIsArabic(!isArabic);
  };

  return (
    <div onClick={toggleTranslation}>
      {translatedText}
    </div>
  );
};

export default TranslateComponent;
