import React, { useContext, useEffect } from 'react';
import { ThemeContext, ThemeProvider } from './ThemeContext';
import Todos from './components/Todos/Todos';
import ThemeToggle from './components/common/Button'; // Path to your ThemeToggle button
import './index.css'; // Your default light theme CSS
import './dark.css'; // Your dark theme CSS

const App = () => {
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    // Update the class on the root element when the theme changes
    const root = window.document.documentElement;
    root.classList.remove('light', 'dark');
    root.classList.add(theme);
  }, [theme]);

  return (
    <ThemeProvider>
      <div className="app">
        <ThemeToggle /> {/* This is your theme toggle button */}
        <section className="todos">
          <header>
            <h1>To-Do List</h1>
          </header>
          <Todos />
        </section>
      </div>
    </ThemeProvider>
  );
};

export default App;
