import { useCallback, useEffect, useState } from 'react';
import { Todo } from '../Todo/Todo';
import { CreateTodo } from './CreateTodo';

const Todos = () => {
  const [todoIds, setTodoIds] = useState([]);
  const [sortPriority, setSortPriority] = useState('all');

  const fetchData = useCallback(async () => {
    // ... existing code
  }, []);

  const handleCreate = useCallback(async (newTodo) => {
    // ... existing code
  }, [fetchData]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const sortTodos = (todos) => {
    if (sortPriority === 'all') {
      return todos;
    }
    // Sort logic can be more complex depending on your data structure
    return todos.sort((a, b) => {
      const priorityValues = { high: 1, medium: 2, low: 3 };
      return priorityValues[a.priority] - priorityValues[b.priority];
    });
  };

  // useEffect for sorting - you can call this when sortPriority changes
  // if your backend provides the sorted list
  useEffect(() => {
    // Fetch sorted todos from backend or sort them locally
    // setTodoIds(sortTodos(todoIds));
  }, [sortPriority, todoIds]);

  return (
    <>
      <div>
        <label>Sort by: </label>
        <select value={sortPriority} onChange={(e) => setSortPriority(e.target.value)}>
          <option value="all">All Priorities</option>
          <option value="high">High Priority</option>
          <option value="medium">Medium Priority</option>
          <option value="low">Low Priority</option>
        </select>
      </div>
      <CreateTodo handleCreate={handleCreate} />
      {todoIds.length === 0 && <p>No todos to display</p>}
      <ul>
        {sortTodos(todoIds).map(todo => (
          <li key={todo._id}>
            <Todo id={todo._id} fetchData={fetchData} />
          </li>
        ))}
      </ul>
    </>
  );
};

export default Todos;
